import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Edit = props => {
    const { id } = useParams();
    let navigate = useNavigate();

    const inisialData = {
        id: null,
        nama: "",
        telpon: "",
        alamat: "",
        status: ""
    };

    const [dataUser, setDataUSer] = useState(inisialData);

    const getData = id => {
        axios.get('http://192.168.1.10/api_react/getIdUser.php?id=' + id)
            .then(response => {
                setDataUSer(response.data[0]);
            })
            .catch(e => {
                console.log(e);
            });
    };

    useEffect(() => {
        if (id)
            getData(id);
    }, [id]);

    const getValueData = event => {
        const { name, value } = event.target;
        setDataUSer({ dataUser, [name]: value });
    };

    const updateData = () => {
        var data = {
            id: dataUser.id,
            nama: dataUser.nama,
            alamat: dataUser.alamat,
            telpon: dataUser.telpon,
            status: dataUser.status
        };
        axios.post("http://192.168.1.10/api_react/updateData.php", data)
            .then(response => {
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    };

    return (
        <div>
            {dataUser ? (
                <div className="edit-form">
                    <h4>Edit Data User</h4>
                    <form>
                        <div className="form-group">
                            <label htmlFor="nama">Nama</label>
                            <input
                                type="text"
                                className="form-control"
                                id="nama"
                                name="nama"
                                value={dataUser.nama}
                                onChange={getValueData}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="telpon">telpon</label>
                            <input
                                type="text"
                                className="form-control"
                                id="telpon"
                                name="telpon"
                                value={dataUser.telpon}
                                onChange={getValueData}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="status">status</label>
                            <input
                                type="text"
                                className="form-control"
                                id="status"
                                name="status"
                                value={dataUser.status}
                                onChange={getValueData}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="alamat">alamat</label>
                            <input
                                type="text"
                                className="form-control"
                                id="alamat"
                                name="alamat"
                                value={dataUser.alamat}
                                onChange={getValueData}
                            />
                        </div>

                    </form>


                    <button className="badge badge-danger mr-2" onClick="">
                        Delete
                    </button>

                    <button
                        type="submit"
                        className="badge badge-success"
                        onClick={() => updateData(dataUser.id)}
                    >
                        Update
                    </button>
                </div>
            ) : (
                <div>
                    <br />
                    <p>Please click on a Tutorial...</p>
                </div>
            )}
        </div>
    )
}

export default Edit
