import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function Tampil() {
    const [dataUser, setDataUser] = useState([]);

    useEffect(() => {
        getDataUser()
    }, []);

    const getDataUser = () => {
        axios.get('http://192.168.1.10/api_react/getUser.php').then(result => {
            console.log(result.data);
            setDataUser(result.data);
        })
    }

    const hapusData = id => {
        axios.get('http://192.168.1.10/api_react/deleteUser.php?id=' + id).then(result => {
            console.log(result.data);
            if (result.data.pesan === 'berhasil') {
                getDataUser()
            }
        })
    }

    return (
        <table className="table table-striped">
            <thead>
                <tr>
                    <th>No</th>
                    <th>Nama</th>
                    <th>Telpon</th>
                    <th>Status</th>
                    <th>aksi</th>
                </tr>
            </thead>
            <tbody>
                {
                    dataUser.map((post) => (
                        <tr key={post.id}>
                            <td>{post}</td>
                            <td>{post.nama}</td>
                            <td>{post.telpon}</td>
                            <td>{post.alamat}</td>
                            <td>
                                <div className="btn-group">
                                    <Link to={`/edit/${post.id}`} className="btn btn-primary">
                                        <i className="fa fa-edit"></i>
                                    </Link>
                                    <button onClick={() => hapusData(post.id)} className="btn btn-danger">
                                        <i className="fa fa-trash"></i>
                                    </button>
                                </div>
                            </td>
                        </tr>
                    ))
                }
            </tbody>
        </table>
    )
}

export default Tampil
