import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Tambah() {
    let navigate = useNavigate();

    const inisialData = {
        nama: "",
        telpon: "",
        alamat: "",
        status: ""
    };

    const [dataUser, setDataUser] = useState(inisialData);

    const getValueData = event => {
        const { name, value } = event.target;
        setDataUser({ dataUser, [name]: value });
        console.log(dataUser);
    };

    const simpanData = () => {
        const data = {
            nama: dataUser.nama,
            telpon: dataUser.telpon,
            alamat: dataUser.telpon,
            status: dataUser.status
        };
        axios.post("http://192.168.1.10/api_react/simpanUser.php", data).then(result => {
            if (result.data.pesan === 'berhasil') {
                navigate("/");
            }
        }).catch(err => {
            console.log(err);
        })
    }

    return (
        <div>
            <div className="edit-form">
                <h4>Simpan Data User</h4>
                <form>
                    <div className="form-group">
                        <label htmlFor="nama">Nama</label>
                        <input
                            type="text"
                            className="form-control"
                            id="nama"
                            name="nama"
                            value={dataUser.nama}
                            onChange={getValueData}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="telpon">telpon</label>
                        <input
                            type="text"
                            className="form-control"
                            id="telpon"
                            name="telpon"
                            value={dataUser.telpon}
                            onChange={getValueData}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="status">status</label>
                        <input
                            type="text"
                            className="form-control"
                            id="status"
                            name="status"
                            value={dataUser.status}
                            onChange={getValueData}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="alamat">alamat</label>
                        <input
                            type="text"
                            className="form-control"
                            id="alamat"
                            name="alamat"
                            value={dataUser.alamat}
                            onChange={getValueData}
                        />
                    </div>

                </form>

                <button
                    type="submit"
                    className="btn btn-success"
                    onClick={simpanData}
                >
                    Update
                </button>
            </div>
        </div>
    )
}

export default Tambah
