import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Edit = props => {
    const { id } = useParams();
    let navigate = useNavigate();

    const inisialData = {
        id: null,
        nama: "",
        telpon: "",
        alamat: "",
        status: ""
    };

    const [dataUser, setDataUSer] = useState(inisialData);

    const getData = id => {
        axios.get('http://192.168.1.10/api_react/getIdUser.php?' + id)
            .then(response => {
                setDataUSer(response.data);
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    };

    useEffect(() => {
        if (id)
            getData(id);
    }, [id]);
    return (
        <div>Edit</div>
    )
}

export default Edit
