import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function Tampil() {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        axios.get('http://192.168.1.8/api_react/getUser.php', {
            headers: {
                'Content-Type': 'application/json',
                'Access-Control-Allow-Origin': '*',
                'Access-Control-Allow-Methods': 'GET,PUT,POST,DELETE,PATCH,OPTIONS',
            },
        }).then(result => {
            console.log(result.data);
            setPosts(result.data);
        })
    }, []);
    return (
        <table className="table">
            <thead>
                <tr>
                    <th className="text-center">No</th>
                    <th className="text-center">Nama</th>
                    <th className="text-center">Telpon</th>
                    <th className="text-center">Status</th>
                    <th className="text-center">Alamat</th>
                </tr>
            </thead>
            <tbody>
                {
                    posts.map(post => (
                        <tr key={post.id}>
                            <td>{post.nama}</td>
                            <td className="text-center">{post.alamat}</td>
                            <td className="text-center">
                                <div className="btn-group">
                                    <Link to={`/edit/${post.id}`} className="btn btn-primary">
                                        <i className="fa fa-edit"></i>
                                    </Link>
                                    <Link to={`/details/${post.id}`} className="btn btn-secondary">
                                        <i className="fa fa-eye"></i>
                                    </Link>
                                    <Link to={`/delete/${post.id}`} className="btn btn-danger">
                                        <i className="fa fa-trash"></i>
                                    </Link>
                                </div>
                            </td>
                        </tr>
                    ))
                }
            </tbody>
        </table>
    )
}

export default Tampil
